clear allclose allclc% Solve [A][x]=[b]% using the Jacobi iteration% Example convergenceA = [-5 2 1; 1 6 2; 3 -3 8];b = [15 25 20]';% Next try the example below% Example no convergence%A = [-5 2 1; 3 -3 8; 1 6 2];%b = [15 20 25]';% initial guessesxold=[0;0;0];% tolerancesmall=1e-6;% max iterationitermax=10000000;% using backslash commandx_matlab=A\b%------% jacobi generic iteration rule:% xnew = Dinv * b - Dinv * R * xold%------D = diag(diag(A));  % diagonal part Dinv = inv(D);      % inverse of DR = A - D;          % the rest of A (R = A - D = L + U)Dinvb = Dinv*b;DinvR = Dinv*R;% do iterationxnew=zeros(length(A),1)ticiter=0;err=inf; % put a large numberwhile err > small && iter < itermax    xnew = Dinvb - DinvR * xold;    err = max(abs(xnew-xold));    xold=xnew;    iter=iter+1;endtoc% ---% print outputdisp('Iteration completed')disp(['Number of iterations = ' num2str(iter)])x_jacobi=xnewmax_error=err