clear allclose allclc% Let's solve Ax=b, % for two cases of A :  A1 and A2A1= [0.0001 2    2 2];A2=[2 2;     2 2.0001];% and% for two cases of b : b and b perturbedb=[4 4]';b_perturb = [4 4.002]';% for case A1: % notice the perturbation in b % only changes the solution a bit.x1 = A1\bx1_perturb = A1\b_perturb% for case A2:% notice the huge difference in x% when b is slightly changed.x2 = A2\bx2_perturb = A2\b_perturb% look at the condition numberCondA1 = cond(A1)CondA2 = cond(A2)% cond(A2) is huge !!! --> ill-conditioned% graphical illustration% well vs illfiguresubplot(1,2,1)xx=[-0.5:0.1:2.5];plot(xx,(4-0.0001*xx)/2,'-r')hold onplot(xx,(4-2*xx)/2,'--k')subplot(1,2,2)plot(xx,(4-2*xx)/2,'-r')hold onplot(xx,(4-2*xx)/2.0001,'--k')